	//criação de camada base osm
	var osm = L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
    //maxZoom: 19,
    attribution: '© OpenStreetMap | ' + 'Desenvolvido por Edgar Dias'
	});
	//criação da camada de bairros
	var bairros = L.geoJSON(bairrosDP,{
		style: myStyle
	})
	
	.bindPopup(function (layer){
		return layer.feature.properties.nome + '<br>Circunscrição: ' + layer.feature.properties.Delegacia;
	});
	
	//criação da camada de busca (search control)
	var delegaciaBairro = new L.LayerGroup();
	
	//criação do mapa com coordenadas como centro, zoom e camadas <layers:osm, Delegacias>
	var map = L.map('map', {
    center: [0.020869, -51.086726],
    zoom: 12,
	layers: [osm, bairros, delegaciaBairro]
	
	});
	
	// map.addLayer(markersLayer);//já inserido acima em layer:[...]
			
	//método pertencente a leaflet-search.js
	map.addControl( new L.Control.Search({layer:delegaciaBairro}));
		
	for(i in dpBairros) {
		let title = dpBairros[i].title,
		bairro = dpBairros[i].bairros,	//valor buscado
		loc = dpBairros[i].coords,//posição do layer
		x = ""
		for (let i of bairro){
		x = i
				
		marker = new L.Marker(new L.latLng(loc), {title: x});//Após o title, insere-se o alvo da busca, no caso x:os nomes dos bairros
		marker.bindPopup(`<a href="http://www.policiacivil.ap.gov.br/conteudo/unidades-policiais/capital" target="_blanck" title="Clicar para visitar Site da Polícia Civil do Amapá para mais informações"><strong>${title}</strong><a>`);
			
		delegaciaBairro.addLayer(marker);
				
		}			
		}

		//layer com visibilidade sob controle do usuário
	var baseMaps = {
    	"OpenStreetMap": osm
	};

	//layer com visibilidade sob controle do usuário
	var overlayMaps = {
   	// "Delegacias de Bairro": delegacias,
	"Bairros": bairros,
	"Delegacias de bairro": delegaciaBairro
	};

		
	// adiciona o controle de layers acima definidos
	var layerControl = L.control.layers(baseMaps, overlayMaps).addTo(map);

	//insere escala ao final
	L.control.scale().addTo(map);
		

	
	
	
